import React from 'react';
import './ProductScreen.css';
import {Link} from 'react-router-dom';

import {useState,useEffect} from 'react';
import {useDispatch,useSelector} from 'react-redux';

import {getProductDetails} from '../redux/actions/productActions';
import {addToCart} from '../redux/actions/cartActions';


function ProductScreen(props,{match,history}) {

    const [qty,setQty]=useState(1);
    const dispatch=useDispatch();

    const productDetails=useSelector(state=>state.getProductDetails);
    const {loading,error, product} =productDetails;

    return (
   
    <div id="container">	
        <div className="product-image">
            <img src={props.location.state.imageUrl} alt="Omar Dsoky" />
        </div>


	<div className="product-details">
    <h1>{props.location.state.name}</h1>
    <span className="hint-star star">
		<i className="fa fa-star" aria-hidden="true"></i>
		<i className="fa fa-star" aria-hidden="true"></i>
		<i className="fa fa-star" aria-hidden="true"></i>
		<i className="fa fa-star-half-o" aria-hidden="true"></i>
		<i className="fa fa-star-o" aria-hidden="true"></i>
	</span>
        <div className="quantite">
            Quantit√©:<span className="quantiteNumber"> 15</span>
            
            <div class="box">Nombre d'articles  : {``}  
                <select>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                </select>
            </div>

        </div>

    <p className="information">" {(props.location.state.description).substring(0,200)}.."</p>

    <div className="control">
    <Link className="btnBuy" to="/Cart">
        <span className="price">{props.location.state.price}$</span>
        <span className="shopping-cart"><i className="fa fa-shopping-cart" aria-hidden="true"></i></span>
        <span className="buy">Acheter</span>
    </Link>

 </div>
 </div>

   

</div>
    )
}

export default ProductScreen
